import React, { useState } from 'react';
import { 
  Container, 
  TextField, 
  Button, 
  Typography, 
  Box,
  Paper,
  Stepper,
  Step,
  StepLabel,
  CircularProgress,
  Alert
} from '@mui/material';

const API_BASE_URL = 'http://127.0.0.1:5000/api';

const steps = [
  'API Key',
  'Initializing',
  'Setting',
  'Characters',
  'Themes',
  'Tone/Genre',
  'Confirm Keywords',
  'Sitcom Concept'
];

const categories = [
  { key: 'setting', label: 'Setting', prompt: 'Enter settings/locations (comma-separated):' },
  { key: 'characters', label: 'Characters', prompt: 'Enter main characters (comma-separated):' },
  { key: 'themes', label: 'Themes', prompt: 'Enter themes/ideas (comma-separated):' },
  { key: 'tone_genre', label: 'Tone/Genre', prompt: 'Enter tone or genre (comma-separated):' }
];

function App() {
  const [activeStep, setActiveStep] = useState(0);
  const [apiKey, setApiKey] = useState('');
  const [keywords, setKeywords] = useState({
    setting: '',
    characters: '',
    themes: '',
    tone_genre: ''
  });
  const [sitcomConcept, setSitcomConcept] = useState('');
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  // For wizard: which category are we on?
  const categoryIndex = activeStep - 2;
  const currentCategory = categories[categoryIndex];

  const handleNext = async () => {
    setError('');
    // Simulate initialization
    if (activeStep === 1) {
      setLoading(true);
      setTimeout(() => {
        setLoading(false);
        setActiveStep((prev) => prev + 1);
      }, 1200);
      return;
    }
    // Call backend to generate sitcom concept
    if (activeStep === 6) {
      setLoading(true);
      try {
        const response = await fetch(`${API_BASE_URL}/generate-concept`, {
          method: 'POST',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify({ apiKey, keywords })
        });
        const data = await response.json();
        if (data.error) throw new Error(data.error);
        setSitcomConcept(data.concept);
        setActiveStep((prev) => prev + 1);
      } catch (err) {
        setError(err.message);
      } finally {
        setLoading(false);
      }
      return;
    }
    setActiveStep((prev) => prev + 1);
  };

  const handleBack = () => {
    setError('');
    setActiveStep((prev) => prev - 1);
  };

  const handleKeywordChange = (category) => (event) => {
    setKeywords({
      ...keywords,
      [category]: event.target.value
    });
  };

  const renderStepContent = (step) => {
    if (step === 0) {
      return (
        <Box>
          <Typography variant="h6" gutterBottom>
            Enter your OpenAI API Key
          </Typography>
          <TextField
            fullWidth
            type="password"
            value={apiKey}
            onChange={(e) => setApiKey(e.target.value)}
            margin="normal"
          />
        </Box>
      );
    }
    if (step === 1) {
      return (
        <Box sx={{ textAlign: 'center', py: 6 }}>
          <CircularProgress sx={{ mb: 2 }} />
          <Typography variant="h6">Initializing embedding model and AI agents...</Typography>
        </Box>
      );
    }
    if (step >= 2 && step <= 5) {
      // One category per step
      return (
        <Box>
          <Typography variant="h6" gutterBottom>
            {currentCategory.label}
          </Typography>
          <TextField
            fullWidth
            label={currentCategory.label}
            value={keywords[currentCategory.key]}
            onChange={handleKeywordChange(currentCategory.key)}
            margin="normal"
            helperText={currentCategory.prompt}
          />
        </Box>
      );
    }
    if (step === 6) {
      // Confirmation step
      return (
        <Box>
          <Typography variant="h6" gutterBottom>
            Confirm Your Keywords
          </Typography>
          <Paper elevation={3} sx={{ p: 2, mt: 2 }}>
            {categories.map((cat) => (
              <Box key={cat.key} sx={{ mb: 1 }}>
                <Typography variant="subtitle1">{cat.label}:</Typography>
                <Typography>{keywords[cat.key]}</Typography>
              </Box>
            ))}
          </Paper>
        </Box>
      );
    }
    if (step === 7) {
      // Sitcom concept step
      return (
        <Box>
          <Typography variant="h6" gutterBottom>
            Generated Sitcom Concept
          </Typography>
          {loading ? (
            <Box sx={{ textAlign: 'center', py: 6 }}>
              <CircularProgress sx={{ mb: 2 }} />
              <Typography>Generating sitcom concept...</Typography>
            </Box>
          ) : (
            <Paper elevation={3} sx={{ p: 2, mt: 2 }}>
              <Typography>{sitcomConcept || 'No concept generated.'}</Typography>
            </Paper>
          )}
        </Box>
      );
    }
    return null;
  };

  return (
    <Container maxWidth="sm" sx={{ py: 4 }}>
      <Typography variant="h4" component="h1" gutterBottom align="center">
        Sitcom Generator
      </Typography>

      <Stepper activeStep={activeStep} sx={{ mb: 4 }}>
        {steps.map((label) => (
          <Step key={label}>
            <StepLabel>{label}</StepLabel>
          </Step>
        ))}
      </Stepper>

      {error && (
        <Alert severity="error" sx={{ mb: 2 }}>
          {error}
        </Alert>
      )}

      {renderStepContent(activeStep)}

      <Box sx={{ display: 'flex', justifyContent: 'space-between', mt: 4 }}>
        <Button
          disabled={activeStep === 0 || loading}
          onClick={handleBack}
        >
          Back
        </Button>
        <Button
          variant="contained"
          onClick={handleNext}
          disabled={
            loading ||
            (activeStep === 0 && !apiKey) ||
            (activeStep >= 2 && activeStep <= 5 && !keywords[currentCategory.key]) ||
            (activeStep === 7 && loading)
          }
        >
          {loading && (activeStep === 1 || activeStep === 6) ? (
            <CircularProgress size={24} color="inherit" />
          ) : (
            activeStep === steps.length - 1 ? 'Finish' : 'Next'
          )}
        </Button>
      </Box>
    </Container>
  );
}

export default App;
